/**
 * A List of Fixed Types to generate Spreadsheet
 */
const TemplateType = Object.freeze({
  Availability: Symbol("Availability"),
  Timesheet: Symbol("Timesheet"),
  Roster: Symbol("Roster"),
  Individuals: Symbol("Individuals")
})

/**
 * A class that creates templates and format all sheets within a particular spreadsheet
 */
class Spreadsheet {
  /**
   * Creation of class must require a spreadsheet file
   */
  constructor(spreadsheet) {
    this.spreadsheet = spreadsheet;
  }

  /**
   * Get Name of the Spreadsheet
   */
  getName() {
    return this.spreadsheet.getName();
  }
  /**
   * Get Sheet By Name
   * @params {string} sheetName: Name of the specific sheet
   * return {Sheet} return a customized class of Sheet 
   */
  getSheet(sheetName) {
    if (typeof sheetName !== "string")
      throw new TypeError("Please choose the correct format");
    let sheet = this.spreadsheet.getSheetByName(sheetName);
    if (!sheet) {
      return null;
    }
    return new Sheet(sheet);
  }

  /**
   * Insert Sheet Sheet
   * @params {string} sheetName: Name of the Month (Also name of the sheet)
   * @return {Sheet} sheet: Returns a New Object Sheet
   */
  insertSheet(sheetName) {
    if ((typeof sheetName !== "string"))
      throw new TypeError("Please choose the correct format!")
    let sheets = this.spreadsheet.getSheets();
    let foundSheet = sheets.find(sheet => sheet.getName() === sheetName);
    if (sheets.length === 1 &&
      sheets[0].getName() === "Sheet1") {
      console.log("Renaming Sheet Name to Month");
      foundSheet = sheets[0].setName(sheetName);
    }
    else if (!foundSheet) {
      console.log("Unable to find the correct sheet! Inserting Sheet into spreadsheet");
      foundSheet = this.spreadsheet.insertSheet(sheetName);
    }
    return new Sheet(foundSheet);
  }

  /**
   * Generats a Sheet template
   * @params {TemplateType} type: A Fixed List that detects type of template to generate
   * @params {string} sheetName: Name of the sheet
   * return {Sheet} sheet: The sheet that was targeted
   */
  generateTemplate(type = TemplateType, sheetName) {
    if ((!type))
      throw new TypeError("Please choose the correct format!")
    if (type === TemplateType.Custom && !Object.keys(args).length)
      throw new Error("Unable to process customized template as no argument is provided")
    let sheet = this.insertSheet(sheetName);
    let year = dateHelper.getYear();
    var rootFolder = folder.retrieveFolderByName(`ESS ${year}`)
    switch (type) {
      case TemplateType.Availability:
        sheet.createAvailabilityTemplate(sheetName);
        sheet.insertAvailabilityData("Individuals", "", sheetName);
        SpreadsheetApp.setActiveSpreadsheet(this.spreadsheet);
        onOpenSpreadsheetTrigger(this.spreadsheet, "createAvailMenu");
        onOpenSpreadsheetTrigger(this.spreadsheet, "createMonthlyAvailSpreadsheet");
        break;
      case TemplateType.Roster:
        sheet.createRosterTemplate(sheetName);
        let masterFile = file.retrieveFileByName("Event Crew");
        let masterSS = new Spreadsheet(SpreadsheetApp.open(masterFile));
        let masterSheet = masterSS.getSheet("Sheet1");
        sheet.insertRosterData("Yearly Availability", masterSheet, rootFolder, sheetName);
        SpreadsheetApp.setActiveSpreadsheet(this.spreadsheet);
        onOpenSpreadsheetTrigger(this.spreadsheet, "createMonthlyRosterSpreadsheet");
        onOpenSpreadsheetTrigger(this.spreadsheet, "createRosterMenu");
        break;
      case TemplateType.Timesheet:
        sheet.createTimesheetTemplate();
        break;
      case TemplateType.Individuals:
        sheet.createIndividualTemplate(sheetName);
        break;
      default:
        throw new SyntaxError("There is an issue taking in the parameter: (type)!");
    }
    return sheet;
  }

  /**
   * Insert Data to Sheet
   * @params {TemplateType} type: A Fixed List that detects type of template to generate
   * @params {string} sheetName: Name of the Sheet
   * @params {Array of JSON} args: Arguments for creation of Timesheet Data
   * Data Sample:
   * [
   *           {
   *            id: 1,
   *            name: "David",
   *            role: ["IC","Usher"],
   *            date: ["1 June","2 June"]
   *           },
   *           {
   *            id: 3,
   *            name: "Aaron",
   *            role: ["IC"],
   *            date: ["1 June"]
   *           },
   *]
   * @returns {Sheet} sheet : A new Object of Sheet;
   */
  insertDataToSheet(type = TemplateType, sheetName, args = []) {
    if ((!type) && typeof sheetName !== "string")
      throw new TypeError("Please choose the correct format!")
    let foundSheet = this.spreadsheet.getSheets().find(sheet => sheet.getName() === sheetName);
    if (!foundSheet)
      throw new Error("Unable to find the sheet Name. Please ensure that the sheet name is correct!");
    let sheet = new Sheet(foundSheet);
    let year = dateHelper.getYear();
    var rootFolder = folder.retrieveFolderByName(`ESS ${year}`)
    switch (type) {
      case TemplateType.Availability:
        sheet.insertAvailabilityData("Individuals", "", sheetName);
        break;
      case TemplateType.Roster:
        let masterFile = file.retrieveFileByName("Event Crew");
        let masterSS = new Spreadsheet(SpreadsheetApp.open(masterFile));
        let masterSheet = masterSS.getSheet("Sheet1");
        sheet.insertRosterData("Yearly Availability", masterSheet, rootFolder, sheetName);
        break;
      case TemplateType.Timesheet:
        sheet.insertTimesheetData(args);
        break;
      default:
        throw new SyntaxError(`There is an issue taking in the parameter: ${type}!`);
    }
    return sheet;


  }
}

/**
* Purpose: 1. Used to generate all data related to dates
*          2. Reduce memories used from creation of new Object
*          3. Reduce amount of Global Functions
*/
const d = new Date();
const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
const dateHelper = class {

  /**
   * Get Current Dates
   * @return {numeric, numeric, numeric} date, month, year : The Current Dates 
   */
  static getCurrentDates() {
    const month = d.getMonth();
    const date = d.getDate();
    const year = d.getFullYear();
    return {date, month, year};
  }

  /**
   * Get Date
   * @params {string} datestring: User input date
   * @return {string} date and month
   */
  static getDate(datestring = "") {
    if (typeof datestring !== "string" && !datestring instanceof Date)
      throw new TypeError("There is some errors in converting to string format!");
    let date = !datestring ? d : new Date(datestring);
    let dates = date.getDate();
    let month = months[date.getMonth()].slice(0,3);
    return `${dates} ${month}`;
  }
  /**
   * Get Next Month
   * @return {string} month: Return Name of the month;
   */
  static getNextMonth() {
    let nextMonth = new Date(d.getFullYear(),d.getMonth() + 1,1).getMonth();
    return months[nextMonth];
  }

  /**
   * Get Month Name.
   * @params {string} datestring: User input date
   * @return {string} return name of month.
   * @return {string} return current month name if datestring is empty.
   * @error returns error if wrong type of input
   */
  static getMonthName(datestring = "") {
    if (typeof datestring !== "string" && !datestring instanceof Date)
      throw new TypeError("There is some errors in converting to string format!");
    if (!datestring) {
      console.log("Getting current month");
      return d.toLocaleString('en-US', { month: 'long' });
    }
    let date = Date.parse(datestring);
    return date.toLocaleString('en-US', { month: "long" });
  }
  /**
   * Get Year.
   * @params {string} datestring: User input date
   * @return {string} return year.
   * @return {string} return current year if datestring is empty.
   * @error returns error if wrong type of input
   */
  static getYear(datestring = "") {
    if (typeof datestring !== "string" && !datestring instanceof Date)
      throw new TypeError("There is some errors in converting to string format!");
    if (!datestring) {
      console.log("Getting current year");
      return d.toLocaleString('en-US', { year: 'numeric' });
    }
    let date = Date.parse(datestring);
    return date.toLocaleString('en-US', { year: "numeric" });
  }

  /**
   * Get Number of days in the specific month
   * @params {string} month: User inputted month
   * @return {Array} return number of days in the month
   * @error returns error if wrong type of input
   */
  static getDaysInMonth(month = "") {
    if (typeof month !== "string" && !month instanceof Date)
      throw new TypeError("There is some errors in converting to string format!");
    if (month === "")
      month = this.getMonthName().slice(0,3);
    let shortMonths = months.map(x => x.slice(0,3));
    let monthIndex = shortMonths.findIndex(shortMon => shortMon === month);
    if (monthIndex == -1)
      throw new Error("Month is not in the correct name " + month);
    let year = d.getFullYear();
    return new Date(year,monthIndex+1,0).getDate()
  }

  /**
   * Get Next Year
   * @return {Date} returns the End of Next Year
   */
  static getNextYear() {
    return new Date(this.getYear() + 1, 1, 1);
  }

  static getYearEnd() {
    return new Date(this.getYear(),11,10);
  }
}

const file = class {
  /**
 * Retrieve a specific File By Name
 * Does not check the based on the parent folder
 * @params {string} fileName : Name of the specific file
 * @params {Folder} rootFolder : The Parent Folder which the specific file exist in
 * @returns {file} returns first file or null if unable to retrieve folder
 */
  static retrieveFileByName(fileName, rootFolder = "") {
    if (typeof fileName !== "string" || (rootFolder && !rootFolder instanceof Object))
      throw new TypeError("There is some errors in retrieving the file!"); 
    if (!rootFolder) {
      var fileIterator = DriveApp.getFilesByName(fileName)
      var file = fileIterator.hasNext() ? fileIterator.next() : null;
    }
    else
      file = miscTools.recursiveSearchFile(fileName,rootFolder);
      return file;
  }
  /**
   * Creates File to Folder
   * @params {string} fileName: Name of the file
   * @params {Folder} rootFolder: The Parent Folder which the specific file is being created
   * @returns {file} returns the file that have been created
   */
  static createSpreadsheetToFolder(fileName, rootFolder = "") {
    if (typeof fileName !== "string" || (rootFolder && !rootFolder instanceof Object))
      throw new TypeError("There is some errors in retrieving the file!");
    let ss = SpreadsheetApp.create(fileName);
    !rootFolder ? DriveApp.getFileById(ss.getId()) : DriveApp.getFileById(ss.getId()).moveTo(rootFolder);
    return ss;
  }

}

const folder = class {
  /**
   * Generate Whole Folder Hierarchy
   * Only Creates Subfolders within the root directory
   * @params {Array of Object} subFolders: An array that contains the parent folder name and a list of subdirectories' name
   * subFolder Example: {parentFolder: rootFolderName, subDirectories: [folder1,folder2,folder3], args: {parentFolder : folder1, subDirectories: [folder4], args: {...args}}} 
   * @params {Folder} rootFolder: Parent Folder of the Folder Hierarchy
   */
  static generateFolderHierarchy(subFolder, rootFolder = "") {
    if ((!subFolder instanceof Object || (rootFolder && !rootFolder instanceof Object)))
      throw new TypeError("Invalid input. Ensure that subFolder follows the format!");
    console.log("Retrieving From Master Folder");
    let parent = !rootFolder ? this.retrieveFolderByName(subFolder.parentFolder) : this.retrieveFolderByName(subFolder.parentFolder, rootFolder);
    console.log("Creating Parent Folder");
    if (!parent)
      parent = !rootFolder ? DriveApp.createFolder(subFolder.parentFolder) : rootFolder.createFolder(subFolder.parentFolder);
    var folderArray = [];
    console.log("Creating Sub Directories")
    subFolder.subDirectories.forEach(name => {
      let child = this.retrieveFolderByName(name, parent);
      !child ?
        folderArray.push(parent.createFolder(name)) :
        folderArray.push(child);
    });
    let args = subFolder.args;
    if (!args)
      return;
    console.log("Creating Directories within Sub Directories")
    miscTools.recursiveFolderCreation(args, folderArray);
  }

  /**
 * Retrieve a specific Folder By Name
 * Does not check the based on the parent folder
 * @params {string} folderName : Name of the specific folder
 * @params {Folder} rootFolder : The Parent Folder which the specific folder exist in
 * @returns {Folder} returns first folder or null if unable to retrieve folder
 */
  static retrieveFolderByName(folderName, rootFolder = "") {
    if (typeof folderName !== "string" || (rootFolder && !rootFolder instanceof Object))
      throw new TypeError("There is some errors in retrieving the folder!");
    if (!rootFolder) {
      var folderIterator = DriveApp.getFoldersByName(folderName)
      var folder = folderIterator.hasNext() ? folderIterator.next() : null;
    }
    else
      folder = miscTools.recursiveSearchFolder(folderName, rootFolder);
    return folder;
  }

  /**
 * Retrieve all files
 * @params {folder} rootFolder: The folder that contains all the spreadsheets 
 * @return {Array[File]} return an array of files within the folder 
 */
  static retrieveAllSpreadsheetsInFolder(rootFolder) {
    if (!rootFolder instanceof Object)
      throw new TypeError("There is some errors in the parameter :rootFolder is not in the right format!");
    let files = [];
    let iterator = rootFolder.getFilesByType(MimeType.GOOGLE_SHEETS);
    while (iterator.hasNext())
      files.push(SpreadsheetApp.open(iterator.next()));
    return files;
  }

  /**
   * Retrieve Folder Hierarchy of specific folder
   * @params {Folder} rootFolder: Targeted Folder
   * @return {Objects} return as an JSON formatted Object
   * Return Value Example: {id: rootFolderID, name:rootFolderName, subDirectories: {id: folder1ID, name: folder1Name, subDirectories: {...args}}} 
   */
  static retrieveFolderHierarchy(rootFolder) {
    if (!rootFolder instanceof Object)
      throw new TypeError("There is some errors in the parameters: rootFolder is not in the right format!");
    return miscTools.recursiveFolder(rootFolder);
  }
}

/**
 * Encapsulation of miscallenous functions
 * Purpose : To ensure that there is no wrong usage of functions (Bypass)
 */
const miscTools = class {
  /**
   * Recursive Function to iterate every folders within Sub Directories and create new folders 
   * @params {Object} args: Sub Directories that contains the parent folder,direct sub directories and nested sub directories
   * @params {Array} folderArray: The list of parent folders
   * Does not return any values
   * Does not throw any errors due to recursion
   */
  static recursiveFolderCreation(args, folderArray) {
    if (!args.length)
      return;
    for (const data of args) {
      let childName = data.parentFolder;
      let childSubFolders = data.subDirectories;
      let nestedData = data.args;
      let folder = folderArray.find(folder => folder.getName() === childName);
      if (!folder)
        continue;
      var childArray = [];
      childSubFolders.forEach(name => {
        let child = folderGenerator.retrieveFolderByName(name, folder);
        (!child) ?
          childArray.push(folder.createFolder(name)) :
          childArray.push(child);
      });
      if (!nestedData)
        continue;
      this.recursiveFolderCreation(nestedData, childArray);
    }
  }
  /**
   * Recursive Function to iterate every folders within Sub Directories
   * @params {Folder} parentFolder: The Parent Folder
   * @return {object} folderHierarchy: A nested JSON Object 
   * Example: {parentFolder: rootFolderID, subDirectories: [folder1ID,folder2ID,folder3ID], args: {parentFolder : folder1ID, subDirectories: [folder4ID], args: {...args}}} 
   */
  static recursiveFolder(parentFolder) {
    if (!parentFolder)
      return;
    let folderHierarchy = { id: parentFolder.getId(), name: parentFolder.getName(), subDirectories: [] };
    let subFolderIterator = parentFolder.getFolders();
    while (subFolderIterator.hasNext()) {
      let subFolder = subFolderIterator.next();
      folderHierarchy.subDirectories.push(this.recursiveFolder(subFolder));
    }
    return folderHierarchy;
  }

  /**
   * Recursive Function to iterate every folders to find file with specific file name
   * @params {string} fileName: Name of the file
   * @params {Folder} parentFolder: The root Folder which is being searched 
   * @return {File} null if nothing is found, else return the file
   */
  static recursiveSearchFile(fileName, parentFolder) {
    if (!parentFolder)
      return null;
    let fileIterator = parentFolder.getFilesByName(fileName);
    while (fileIterator.hasNext())
      return fileIterator.next();
    let subFolderIterator = parentFolder.getFolders();
    while (subFolderIterator.hasNext()) {
      let subFolder = subFolderIterator.next();
      let file = this.recursiveSearchFile(fileName, subFolder);
      if (file)
        return file;
    }
    return null;
  }

  /**
 * Recursive Function to iterate every folders to find folder with specific folder name
 * @params {string} folderName: Name of the folder
 * @params {Folder} parentFolder: The root Folder which is being searched 
 * @return {Folder} null if nothing is found, else return the folder
 */
  static recursiveSearchFolder(folderName, parentFolder) {
    if (!parentFolder)
      return null;
    if (String(parentFolder.getName()) === String(folderName))
      return parentFolder;
    let subFolderIterator = parentFolder.getFolders();
    while (subFolderIterator.hasNext()) {
      let subFolder = subFolderIterator.next();
      let folder = this.recursiveSearchFolder(folderName, subFolder);
      if (folder)
        return folder;
    }
    return null;
  }
}

